好多都是第三方的，自己闲麻烦，自己就整理了一套自己一个rss类，对需要的朋友给个帮助吧，也方便了自己，呵呵 直接贴代码： &lt;?phpclass Rss extends CController&#123; //public public $rss_ver = &quot;2.0&quot;; public $channel_title = &#x27;&#x27;; public $channel_link = &#x27;&#x27;; public $channel_description = &#x27;&#x27;; public $language = &#x27;zh_CN&#x27;; public $copyright = &#x27;&#x27;; public $webMaster = &#x27;&#x27;; public $pubDate = &#x27;&#x27;; public $lastBuildDate = &#x27;&#x27;; public $generator = &#x27;GoWhich RSS Generator&#x27;; public $content = &#x27;&#x27;; public $items = []; /** * 添加基本信息 * @param string $title * @param string $link * @param string $description */ public function __construct($title, $link, $description) &#123; $this-&gt;channel_title = $title; $this-&gt;channel_link = $link; $this-&gt;channel_description = $description; $this-&gt;pubDate = Date(&#x27;Y-m-d H:i:s&#x27;, time()); $this-&gt;lastBuildDate = Date(&#x27;Y-m-d H:i:s&#x27;, time()); &#125; /** * 添加一个节点 * @param string $title * @param string $link * @param string $description * @param date $pubDate */ public function addItem($title, $link, $description, $pubDate) &#123; $this-&gt;items[] = [&#x27;title&#x27; =&gt; $title, &#x27;link&#x27; =&gt; $link, &#x27;descrīption&#x27; =&gt; $description, &#x27;pubDate&#x27; =&gt; $pubDate]; &#125; /** * 构建xml元素 */ public function buildRSS() &#123; $s = &lt;&lt;&lt;RSS&lt;?xml version=&#x27;1.0&#x27; encoding=&#x27;utf-8&#x27;?&gt;
&lt;rss version=&quot;2.0&quot;&gt;
RSS; // start channel $s .= &quot;&lt;channel&gt;
&quot;; $s .= &quot;&lt;title&gt;&lt;![CDATA[&#123;$this-&gt;channel_title&#125;]]&gt;&lt;/title&gt;
&quot;; $s .= &quot;&lt;link&gt;&lt;![CDATA[&#123;$this-&gt;channel_link&#125;]]&gt;&lt;/link&gt;
&quot;; $s .= &quot;&lt;descrīption&gt;&lt;![CDATA[&#123;$this-&gt;channel_description&#125;]]&gt;&lt;/descrīption&gt;
&quot;; $s .= &quot;&lt;language&gt;&#123;$this-&gt;language&#125;&lt;/language&gt;
&quot;; if (!empty($this-&gt;copyright)) &#123; $s .= &quot;&lt;copyright&gt;&lt;![CDATA[&#123;$this-&gt;copyright&#125;]]&gt;&lt;/copyright&gt;
&quot;; &#125; if (!empty($this-&gt;webMaster)) &#123; $s .= &quot;&lt;webMaster&gt;&lt;![CDATA[&#123;$this-&gt;webMaster&#125;]]&gt;&lt;/webMaster&gt;
&quot;; &#125; if (!empty($this-&gt;pubDate)) &#123; $s .= &quot;&lt;pubDate&gt;&#123;$this-&gt;pubDate&#125;&lt;/pubDate&gt;
&quot;; &#125; if (!empty($this-&gt;lastBuildDate)) &#123; $s .= &quot;&lt;lastBuildDate&gt;&#123;$this-&gt;lastBuildDate&#125;&lt;/lastBuildDate&gt;
&quot;; &#125; if (!empty($this-&gt;generator)) &#123; $s .= &quot;&lt;generator&gt;&#123;$this-&gt;generator&#125;&lt;/generator&gt;
&quot;; &#125; // start items for ($i = 0; $i &lt; count($this-&gt;items); $i++) &#123; $s .= &quot;&lt;item&gt;
&quot;; $s .= &quot;&lt;title&gt;&lt;![CDATA[&#123;$this-&gt;items[$i][&#x27;title&#x27;]&#125;]]&gt;&lt;/title&gt;
&quot;; $s .= &quot;&lt;link&gt;&lt;![CDATA[&#123;$this-&gt;items[$i][&#x27;link&#x27;]&#125;]]&gt;&lt;/link&gt;
&quot;; $s .= &quot;&lt;descrīption&gt;&lt;![CDATA[&#123;$this-&gt;items[$i][&#x27;descrīption&#x27;]&#125;]]&gt;&lt;/descrīption&gt;
&quot;; $s .= &quot;&lt;pubDate&gt;&#123;$this-&gt;items[$i][&#x27;pubDate&#x27;]&#125;&lt;/pubDate&gt;
&quot;; $s .= &quot;&lt;/item&gt;
&quot;; &#125; // close channel $s .= &quot;&lt;/channel&gt;
&lt;/rss&gt;&quot;; $this-&gt;content = $s; &#125; /** * 输出rss内容 */ public function show() &#123; if (empty($this-&gt;content)) &#123; $this-&gt;buildRSS(); &#125; return $this-&gt;content; &#125; /** * 设置版权 * @param unknown $copyright */ public function setCopyRight($copyright) &#123; $this-&gt;copyright = $copyright; &#125; /** * 设置管理员 * @param unknown $master */ public function setWebMaster($master) &#123; $this-&gt;webMaster = $master; &#125; /** * 设置发布时间 * @param date $date */ public function setpubDate($date) &#123; $this-&gt;pubDate = $date; &#125; /** * 设置建立时间 * @param unknown $date */ public function setLastBuildDate($date) &#123; $this-&gt;lastBuildDate = $date; &#125; /** * 将rss保存为文件 * @param String $fname * @return boolean */ public function saveToFile($fname) &#123; $handle = fopen($fname, &#x27;wb&#x27;); if (false === $handle) &#123; return false; &#125; fwrite($handle, $this-&gt;content); fclose($handle); &#125; /** * 获取文件的内容 * @param String $fname * @return boolean */ public function getFile($fname) &#123; $handle = fopen($fname, &#x27;r&#x27;); if (false === $handle) &#123; return false; &#125; while (!feof($handle)) &#123; echo fgets($handle); &#125; fclose($handle); &#125;&#125; 如果还是不都清楚可以到github上自己下载好了 github地址：https://github.com/zhangda89/php-library/blob/master/Rss.php
